mapper.select:
select u1.name trader,u2.name agent,u3.name salesman,u1.phone traderphone,u2.phone agentphone,u3.phone salesmanphone,c.customername,r1.region capital,r2.region city,r3.region district from client c,role  r, user u1,user u2,user u3,user_role s,region r1 ,region r2 ,region r3  where  r.roleid=s.roleid and u1.id =s.userid and  c.capitalid=r1.id and c.cityid=r2.id and c.districtid=r3.id and r1.id=r2.pid and r2.id=r3.pid and c.traderid=u1.id  and c.salesmanid=u2.id and c.agentid=u3.id

   and  c.salesmanid=#{p.salesman}


     and c.traderid =#{p.trader}


     and c.agentid= #{p.agent}


    and c.capital=#{p.capital}


    and c.city=#{p.city}


     and c.customername like #{p.firm}

limit #{page},#{size}

select * from deal d,declar r where d.declareid=r.id and r.usersid=#{id}

  and   d.balancedate like #{p.year}
 order by d.balancedate desc
